from django.db import models
from django.contrib.auth.models import User
from django.urls import reverse

# Create your models here.
class Recipe(models.Model):
    user_name = models.ForeignKey(User,on_delete=models.CASCADE,default=1)
    recipe_name = models.CharField(max_length=200, default='Recipe Name' )
    recipe_desc = models.TextField(max_length=10000, default='Recipe detail')
    recipe_img = models.CharField(max_length=1000, default="data:image/jpeg;base64,")
    
    def get_absolute_url(self):
        return reverse("FoodApp:detail", kwargs={"pk": self.pk})
    
    def __str__(self):
        return self.recipe_name